import React from 'react'

class RaceInfo extends React.Component{

    handleClick = () => {
        this.props.saveClass(this.props.charClass.name)
    }

    render(){
        const asiLi = this.props.charRace.asi.map(asi => <li>{asi.attributes[0]} - {asi.value}</li>)

        const raceDesc = this.props.charRace.desc.split('##')
        console.log(this.props.charRace)
        console.log(this.props.charRace.subraces[0])
        return(
            <div className="container box">
                {/* {this.props.charRace.subraces ? <h2 className='title'>{this.props.charRace.subraces[0].name}</h2> : <h2 className='title'>{this.props.charRace.name}</h2>} */}
                <button onClick={this.handleClick}>Choose Race</button>
                <p>{raceDesc}</p>
                <p>Age: {this.props.charRace.age}</p>
                <p>Ability Score Increase:</p>
                <p>{this.props.charRace.asi_desc}</p><ul>{asiLi}</ul>
                <p>Languages: {this.props.charRace.languages}</p>
                <p>Size: {this.props.charRace.size}</p>
                <p>Speed: {this.props.charRace.speed.walk} {this.props.charRace.speed_desc}</p>
                {/* {this.props.charRace.subraces ? this.props.charRace.subraces.map(subrace => )} */}
            </div>
        )
    }
}

export default RaceInfo
