    checkCharacters = () => {
        fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
        .then(r => r.json())
        .then(user => this.setState({
            AllCharacters: user.characters
        }, () => {
            for(let i = 0; i < this.state.AllCharacters.length; i++){
                if(this.state.AllCharacters[i].race.length > 0 && this.state.AllCharacters[i].class.length > 0){
                    console.log('checked')
                } else {
                    fetch(`http://localhost:3000/characters/${this.state.AllCharacters[i].id}`, {
                        method: 'DELETE'
                    }).then(r => r.json()).then(console.log('deleted'))
                }
            }
        }))
    }

    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/')
    }



    render(){
        return(
            <div className="columns container box field is-grouped" style={{marginTop: 30, marginLeft: 350, backgroundImage: `url(http://fanaru.com/fantasy-art/image/232259-fantasy-art-a-burning-rose.gif)`, backgroundSize: 'cover'}}>
                    <Link className="column control is-white is-small" style={{margin: 50}}to={{pathname:'./profile'}} >Profile Page</Link>
                    <Link className="column control is-white is-small" style={{margin: 50}} to={{pathname:'./NewGame'}} >New Game</Link>
                    <Link className="column control is-white is-small" style={{margin: 50}} to={{pathname:'./AllCharacters'}} >All Characters</Link>
                    <Link className="column control is-white is-small" style={{margin: 50}} to={{pathname:'./AllGames'}} >All Games</Link>
                    <Link className="column control is-white is-small" style={{margin: 50}} to={{pathname:'./NewClass'}} >Create New Character</Link>
                    <Link className="column control is-white is-small" style={{margin: 50}} onCLick={this.handleLogout}>Logout</Link>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
        newCharClass: store.newCharClass,
        newCharRace: store.newCharRace,
        stats: store.stats,
        mods: store.mods,
        skills: store.skills
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      AddClass: (charClass) => {
          dispatch({ type: 'ADD_CLASS', attribute: charClass })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NavBar);
