import React, { Component } from 'react';
import { connect } from 'react-redux'
import {Link} from 'react-router-dom'
import NavBar from './NavBar';


class ProfilePage extends Component {

    state = {
        username: '',
        myGames: []
    }

  handleClick = () => {
    localStorage.clear()
    this.props.history.push('/')
  }

  componentDidMount(){
    fetch('http://localhost:3000/profile',{
    headers: {
      'Authorization': `Bearer ${localStorage.token}`
    }
    })
    .then(res => res.json())
    .then(user => 
      {this.props.LogIn(user)

        fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
        .then(r => r.json()).then(user => this.props.SelectCharacter(user.characters[0]))}
    )
    localStorage.removeItem('newCharClass')
    localStorage.removeItem('newCharRace')
    localStorage.removeItem('stats')
    localStorage.removeItem('mods')
    localStorage.removeItem('skills')
    this.checkCharacters()
    fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
        .then(r => r.json()).then(user => this.getGames(user.games))
  }

  getGames = (games) => {
    const gameArr = games.filter(function(ele){
      if(ele.game.length === 1){
        return ele
      }
    })
    this.setState({
      myGames: gameArr
    })
  }

  checkCharacters = () => {
    fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
    .then(r => r.json())
    .then(user => this.setState({
        AllCharacters: user.characters
    }, () => {
        for(let i = 0; i < this.state.AllCharacters.length; i++){
            if(this.state.AllCharacters[i].race.length > 0 && this.state.AllCharacters[i].class.length > 0){
                console.log('checked')
            } else {
                fetch(`http://localhost:3000/characters/${this.state.AllCharacters[i].id}`, {
                    method: 'DELETE'
                }).then(r => r.json()).then(console.log('deleted'))
            }
        }
    }))
  }



  render() {
    console.log(this.state)
    return (
      <div className="container">
        <NavBar />
        <button onClick={this.handleClick}>Logout</button>
        {
          this.props.currentUser.username ?
          <h1 className="title">Welcome {this.props.currentUser.username}!</h1> :
          <h1>getting your info...</h1>

        }

      </div>
    );
  }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
      SelectedCharacter: store.SelectedCharacter
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      SelectCharacter: (character) => {
          dispatch({
              type: 'CHARACTER_SELECT', character
          })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ProfilePage);
