import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'
import Request from'./Request'



class Inbox extends React.Component{
    // state = {
    //     requests: []
    // }

    // componentDidMount(){
    //     for(let i=0; i < this.props.location.state.requests.length; i++){
    //         fetch(`http://localhost:3000/requests/${this.props.location.state.requests[i].id}`)
    //         .then(r => r.json())
    //         .then(request => this.setState({
    //             requests: [...this.state.requests, request]
    //         }))
    //     }
    // }


    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/')
    }



    render(){
        console.log(this.props.location.state.requests)
        const requestComps = this.props.location.state.requests.map( request => <Request request={request}/>)
        return(
            <div className="container">
                <NavBar />
                <button onClick={this.handleLogout} className="button is-black" style={{marginLeft: 100}}>Logout</button>
                <div className="title">
                    <h1>Inbox:</h1>
                    {this.props.requests ? requestComps : <p>You have no requests at this time</p>}

                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Inbox);
