import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'
import CharacterSelect from './CharacterSelect'
import Campaign from './Campaign';
import PartyMember from './PartyMember'
import { Link, Route} from 'react-router-dom'



class AllGames extends React.Component{

    state = {
        AllGames: [],
        myCharacters: []
    }

    componentDidMount(){
        fetch(`http://localhost:3000/games`)
        .then(r => r.json()).then(games => this.setState({
            AllGames: games
        }))
        fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
        .then(r => r.json()).then(user => this.setState({
            myCharacters: user.characters
        }))
    }

    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/')
    }

    handleJoin = (e) => {
        fetch('http://localhost:3000/party_members', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
            },
            body: JSON.stringify({
                game_id: e.target.value,
                character_id: this.props.selectedCharacter.id
            })
        }).then(r => r.json()).then(console.log)
    }

    componentDidUpdate(){
        fetch(`http://localhost:3000/games`)
        .then(r => r.json()).then(games => this.setState({
            AllGames: games
        }))
    }

    getImage = (charClass) => {
        console.log(charClass)
    }




    render(){
        console.log(this.state.AllGames)
        const AllGames = this.state.AllGames.map(game => 
            <div><div key={game.id} className="box columns">
                <div className="column">
                    <h1>Campaign Name:</h1>
                    <p>{game.name}</p>
                    <h2>Description</h2>
                    <p>{game.desc}</p>
                </div>
                <div className="column">
                    <h2>Loscation:</h2>
                    {game.location}
                    <h2>EXP Level:</h2>
                    <p>{game.explevel}</p>
                    <h2>Role Playing Intensity:</h2>
                    <p>{game.rplevel}</p>
                    <h3>Created by:</h3>
                    <p>{game.dungeon_master.name}</p>
                </div>
                <div className="column">
                    <h2>Free Spots: {6 - game.party.length}</h2>
                    <h2>Party Members:</h2><br></br>
                    <div className="columns container" style={{width: 1300}}>
                        {game.party.length > 0 &&  game.players.length > 0 ? <div className="column">
                            <PartyMember character={game.party[0]} player={game.players[0]}/>
                            </div> : null}
                        {game.party.length > 1 &&  game.players.length > 1 ? <div className="column">
                            <PartyMember character={game.party[1]} player={game.players[0]}/>
                        </div> : null }
                        {game.party.length > 2 &&  game.players.length > 2 ? <div className="column">
                            <PartyMember character={game.party[2]} player={game.players[0]}/>
                        </div> : null}
                        {game.party.length > 3 &&  game.players.length > 3 ? <div className="column">
                            <PartyMember character={game.party[3]} player={game.players[0]}/>
                        </div> : null }
                        {game.party.length > 4 &&  game.players.length > 4 ? <div className="column">
                            <PartyMember character={game.party[4]} player={game.players[0]}/>
                        </div> : null}
                        {game.party.length > 5 &&  game.players.length > 5 ? <div className="column">
                            <PartyMember character={game.party[5]} player={game.players[0]}/>
                        </div> : null }
                    </div>
                </div>
            </div>
            <div>{game.party.length < 6 && this.props.selectedCharacter && !game.party_ids.includes(this.props.currentUser.id) ? 
                <div className="box column" style={{ width: 150 }}>
                    <h1>Join using:</h1>
                    <p>{this.props.selectedCharacter.name}</p>
                    <p>Class: {this.props.selectedCharacter.class[0].name}</p>
                    <p>Race: {this.props.selectedCharacter.race[0].name}</p>
                    <p>Alignment: {this.props.selectedCharacter.alignment}</p>
                    <button onClick={this.handleJoin} value={game.id}>Join Game</button></div>
                 : null}
                 {game.party_ids.includes(this.props.currentUser.id) || game.dungeon_master.user_id === this.props.currentUser.id ? 
                 <Link to={{pathname:"/Campaign", state: { game: game }}} >
                    <button>Go To Campaign</button>
                    </Link> : null}
                 </div><br></br>
            </div>
        )
        return(
            <div>
                <NavBar />
                <CharacterSelect characters={this.state.myCharacters} handleCharChange={this.handleCharChange}/>
                <button onClick={this.handleLogout}>Logout</button>
                <h1>All Games:</h1><br></br>
                {AllGames}
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
        selectedCharacter: store.selectedCharacter
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      SelectCharacter: (character) => {
          dispatch({
              type: 'CHARACTER_SELECT', character
          })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AllGames);
