export default (state={ currentUser: {}, newCharAtr: [] }, action) => {
    // console.log("our reducer's state: ", state)
    // console.log("our reducer's action: ", action)

    switch (action.type) {
      case "LOGIN_CURRENT_USER":
        return { currentUser: action.user }
      case "ADD_ATR":
        return { newCharClass: action.attribute }

      default:
        return state
    }
  }
