import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'



class AllGames extends React.Component{

    state = {
        AllGames: []
    }

    componentDidMount(){
        fetch(`http://localhost:3000/games`)
        .then(r => r.json()).then(games => this.setState({
            AllGames: games
        }))
    }

    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/login')
    }





    render(){
        console.log(this.state)
        const AllGames = this.state.AllGames.map(game => {
            <div key={game.id} className="box">
                <h1>Campaign Name</h1>
                <p>{game.name}</p>
                <h2>EXP Level:</h2>
                <p>{game.explevel}</p>
                <h2>Role Playing Intensity</h2>
                <p>{game.rplevel}</p>
                <h3>Created by:</h3>
                <p>{game.dungeon_master.name}</p>
            </div>
        })
        return(
            <div>
                <NavBar />
                <button onClick={this.handleLogout}>Logout</button>
                <h1>All Games:</h1>
                {AllGames}
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AllGames);
