import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'



class AllCaracters extends React.Component{

    state = {
        character: {},
        savingProf: {
            strength: 0,
            dexterity: 0,
            constitution: 0,
            intelligence: 0,
            wisdom: 0,
            charisma: 0
        },
    }

    componentWillMount(){
        fetch(`http://localhost:3000/characters/${this.props.location.state.character.id}`)
        .then(r => r.json())
        .then(character => this.setState({
            character: character
        }))
        // const savingProf = this.props.newCharClass.prof_saving_throws.toLowerCase().split(', ')
        // this.setState({
        //     savingProf: {
        //         ...this.state.savingProf,
        //         [savingProf[0]]: 2,
        //         [savingProf[1]]: 2
        //     }
        // })
    }

    getClassImg = (charClass) => {
        if(charClass === 'Rogue'){
            return require('./hooded-assassin.png')
        } else if(charClass === 'Barbarian'){
            return require('./barbarian.png')
        } else if(charClass === 'Paladin'){
            return require('./elf-helmet.png')
        } else if(charClass === 'Warlock'){
            return require('./warlock-hood.png')
        } else if(charClass === 'Ranger'){
            return require('./cowled.png')
        } else if(charClass === 'Bard'){
            return require('./musical-notes.png')
        } else if(charClass === 'Cleric'){
            return require('./pope-crown.png')
        } else if(charClass === 'Druid'){
            return require('./wolf-head.png')
        } else if(charClass === 'Fighter'){
            return require('./swordman.png')
        } else if(charClass === 'Monk'){
            return require('./monk-face.png')
        } else if(charClass === 'Sorcerer'){
            return require('./robe.png')
        } else if(charClass === 'Wizard'){
            return require('./wizard-staff.png')
        }
    }

    getRaceImg = (charRace) => {
        if(charRace === 'Dwarf'){
            return require('./dwarf-king.png')
        } else if(charRace === 'Elf'){
            return require('./woman-elf-face.png')
        } else if(charRace === 'Halfling'){
            return require('./hobbit-dwelling.png')
        } else if(charRace === 'Human'){
            return require('./sensuousness.png')
        } else if(charRace === 'Dragonborn'){
            return require('./spiked-dragon-head.png')
        } else if(charRace === 'Gnome'){
            return require('./bad-gnome.png')
        } else if(charRace === 'Half-Elf'){
            return require('./elf-ear.png')
        } else if(charRace === 'Half-Orc'){
            return require('./orc-head.png')
        } else if(charRace === 'Tiefling'){
            return require('./horned-reptile.png')
        } 
    }

    // componentDidMount(){
    //     fetch(`http://localhost:3000/characters/${this.props.location.state.character.id}`)
    //     .then(r => r.json())
    //     .then(character => this.setState({
    //         character: character
    //     }))

    // }


    render(){
        console.log(this.state)
        const clasImg = this.getClassImg(this.state.character.class[0].name)
        const raceImg = this.getRaceImg(this.state.character.race[0].name)
        return(
            <div className="container">
                <NavBar />
                <button onClick={this.handleLogout} className="button is-black" style={{marginLeft: 100}}>Logout</button>
                <div className="container box">
                    <div className="box columns">
                        <div className="column is-4">
                        <h1 className="subtitle">
                            Name: 
                        </h1>
                        <p className="title">{this.state.character.name}</p>
                        </div>
                        <div className="column is-4">
                            <h1 className="subtitle">
                                Class:
                            </h1>
                            <img src={clasImg} className="image is-96x96" />
                            <p>{this.state.character.class[0].name}</p>
                        </div>
                        <div className="column  is-4">
                            <h1 className="subtitle">
                                Race:
                            </h1>
                            <img src={raceImg} className="image is-96x96" />
                            <p>{this.state.character.race[0].name}</p>
                        </div>
                    </div>
                    <div className="columns">
                        <div className="column is-2">
                        <div className="column content" >
                            <ul>
                            <h2>Stats:</h2>
                            <p>Strength - {this.state.character.stats[0].strength}</p><br></br>
                            <p className="box" style={{width: 60}}>{this.state.character.mods[0].strmod}</p><br></br>
                            <p>Dexterity - {this.state.character.stats[0].dexterity}</p><br></br>
                            <p className="box" style={{width: 60}}>{this.state.character.mods[0].dexmod}</p><br></br>
                            <p>Constitution - {this.state.character.stats[0].constitution}</p><br></br>
                            <p className="box" style={{width: 60}}>{this.state.character.mods[0].conmod}</p><br></br>
                            <p>Intelligence - {this.state.character.stats[0].intelligence}</p><br></br>
                            <p className="box" style={{width: 60}}>{this.state.character.mods[0].intmod}</p><br></br>
                            <p>Wisdom - {this.state.character.stats[0].wisdom}</p><br></br>
                            <p className="box" style={{width: 60}}>{this.state.character.mods[0].wismod}</p><br></br>
                            <p>Charisma - {this.state.character.stats[0].charisma}</p><br></br>
                            <p className="box" style={{width: 60}}>{this.state.character.mods[0].chrmod}</p><br></br>
                            </ul> 
                        </div>
                        </div>
                        <div className="column is-5">
                        <p>Proficiency Modifier:</p>
                        <div className="box" style={{width: 180}}>+ 2</div>
                        </div>
                        {/* <ul className="box" style={{width: 180}}>
                            <p>Saving Throws:</p>
                            <li>Strength: {this.props.location.state.character.strmod + this.state.savingProf.strength}</li>
                            <li>Dexterity: {this.props.location.state.character.dexmod + this.state.savingProf.dexterity}</li>
                            <li>Constitution: {this.props.location.state.character.conmod + this.state.savingProf.constitution}</li>
                            <li>Intelligence: {this.props.location.state.character.intmod + this.state.savingProf.intelligence}</li>
                            <li>Wisdom: {this.props.location.state.character.wismod + this.state.savingProf.wisdom}</li>
                            <li>Charisma: {this.props.location.state.character.chrmod + this.state.savingProf.charisma}</li>
                        </ul> */}
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
        selectedCharacter: store.selectedCharacter
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AllCaracters);
