import React from  'react'
import { connect } from 'react-redux'
import ReactDice from 'react-dice-complete'
import StatDist from './StatDist'
import 'react-dice-complete/dist/react-dice-complete.css'


class NewCharacter extends React.Component{
    state = {
        strength: 0,
        dexterity: 0,
        constitution: 0,
        intelligence: 0,
        wisdom: 0,
        charisma: 0,
        statRoll: [],
        statVal: []
    }

    // componentDidMount(){
    //     this.setState({
    //         strength: this.props.strength,
    //         dexterity: this.props.dexterity,
    //         constitution: this.props.constitution,
    //         intelligence: this.props.intelligence,
    //         wisdom: this.props.wisdom,
    //         charisma: this.props.charisma
    //     })
    // }


    rollAll() {
        this.reactDice.rollAll()

    }


    rollDoneCallback = () => {
        if(this.state.statRoll.length === 4){
            return

        } else {
            // console.log(`You rolled a ${num}`)
            const num = Math.floor(Math.random() * 6) + 1
            this.setState({
                statRoll: [...this.state.statRoll, num]
            })
            this.statValCheck()
        }
    }

    statValCheck = () => {
        if(this.state.statRoll.length === 4){
            const smallest = Math.min.apply(null, this.state.statRoll)
            const pos = this.state.statRoll.indexOf(smallest)
            const topThree = this.state.statRoll.slice(0, pos).concat(this.state.statRoll.slice(pos + 1))
            const statVal = topThree.reduce((a, b) => a + b, 0)
            this.setState({
                statVal: [...this.state.statVal, statVal],
                statRoll: []
            })
        } else {
            return
        }
    }

    getStat =(stat) => {
        this.setState({
            [stat]: this.state.statVal[0],
            statVal: this.state.statVal.concat(this.state.statVal.splice(0, 1))
        })
    }

    handleNext = () => {
        this.props.AddStrength(this.state.strength)
        this.props.AddDexterity(this.state.dexterity)
        this.props.AddConstitution(this.state.constitution)
        this.props.AddIntelligence(this.state.intelligence)
        this.props.AddWisdom(this.state.wisdom)
        this.props.AddCharisma(this.state.strength)
    }


    render(){
            console.log(this.state)
        return(
            <div className="columns">
                <div className="column">
                <h1>Roll Your Stats:</h1>
                <h2>Chosen Race: {this.props.newCharRace.name}</h2>
                <h2>Chosen Class: {this.props.newCharClass.name}</h2>
                <ul>
                <h2>Stats:</h2>{this.state.strength > 0 &&
                this.state.charisma > 0 &&
                this.state.constitution > 0 &&
                this.state.intelligence > 0 &&
                this.state.wisdom > 0 &&
                this.state.dexterity > 0 ?
                <button onClick={this.handleNext}>Confirm</button> : null
                }
                <li>Strength</li>
                <li className="box" style={{width: 60}}>{this.state.strength}</li>
                <li>Dexterity</li>
                <li className="box" style={{width: 60}}>{this.state.dexterity}</li>
                <li>Constitution</li>
                <li className="box" style={{width: 60}}>{this.state.constitution}</li>
                <li>Intelligence</li>
                <li className="box" style={{width: 60}}>{this.state.intelligence}</li>
                <li>Wisdom</li>
                <li className="box" style={{width: 60}}>{this.state.wisdom}</li>
                <li>Charisma</li>
                <li className="box" style={{width: 60}}>{this.state.charisma}</li>
                </ul> </div>
                <div className="column">
                <ReactDice
                    numDice={1}
                    rollDone={this.rollDoneCallback}
                    ref={dice => this.reactDice = dice}
                    faceColor={'#696969'}
                    dotColor={'#D3D3D3'}
                    />
                </div>
                <div className="column">
                <ReactDice
                    numDice={1}
                    rollDone={this.rollDoneCallback}
                    ref={dice => this.reactDice = dice}
                    faceColor={'#696969'}
                    dotColor={'#D3D3D3'}
                    />
                </div>
                {this.state.statVal && this.state.statVal.length < 6 ? 
                    <div style={{paddingTop: 400, paddingRight: 100}}>
                        <h1>Your Rolls: {this.state.statVal.map(stat => `${stat} `)}</h1>
                    </div>  
                    : null  
                }
                {this.state.statVal.length === 6 ? 
                <div style={{paddingTop: 400, paddingRight: 100}}>
                    <h1>Your Rolls: {this.state.statVal.map(stat => `${stat} `)}</h1>
                    <h2>Add {this.state.statVal[0]} to: </h2>
                    <StatDist statVals={this.state.statVal} wisdom={this.state.wisdom} strength={this.state.strength} charisma={this.state.charisma} intelligence={this.state.intelligence} dexterity={this.state.dexterity} constitution={this.state.constitution} getStat={this.getStat}/>
                </div> 
                : null
                }
                <div className="column">
                <ReactDice
                    numDice={1}
                    rollDone={this.rollDoneCallback}
                    ref={dice => this.reactDice = dice}
                    faceColor={'#696969'}
                    dotColor={'#D3D3D3'}
                    />
                </div>
                <div className="column">
                <ReactDice
                    numDice={1}
                    rollDone={this.rollDoneCallback}
                    ref={dice => this.reactDice = dice}
                    faceColor={'#696969'}
                    dotColor={'#D3D3D3'}
                    />
                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
      newCharClass: store.newCharClass,
      newCharRace: store.newCharRace,
      strength: store.strength,
      constitution: store.constitution,
      dexterity: store.dexterity,
      intelligence: store.intelligence,
      wisdom: store.wisdom,
      charisma: store.charisma
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      AddRace: (charRace) => {
          dispatch({ type: 'ADD_Race', attribute: charRace })
      },
      AddStrength: (stat) => {
          dispatch({ type: 'ADD_strength', strength: stat})
      },
      AddDexterity: (stat) => {
          dispatch({ type: 'ADD_dexterity', dexterity: stat})
      },
      AddConstitution: (stat) => {
        dispatch({ type: 'ADD_constitution', constitution: stat})
      },
      AddIntelligence: (stat) => {
        dispatch({ type: 'ADD_intelligence', intelligence: stat})
      },
      AddWisdom: (stat) => {
        dispatch({ type: 'ADD_wisdom', wisdom: stat})
      },
      AddCharisma: (stat) => {
        dispatch({ type: 'ADD_charisma', charisma: stat})
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NewCharacter);
