import React from  'react'
import { connect } from 'react-redux'
import PartyMember from './PartyMember'



class Inbox extends React.Component{
    // state = {
    //     requests: []
    // }

    // componentDidMount(){
    //     for(let i=0; i < this.props.location.state.requests.length; i++){
    //         fetch(`http://localhost:3000/requests/${this.props.location.state.requests[i].id}`)
    //         .then(r => r.json())
    //         .then(request => this.setState({
    //             requests: [...this.state.requests, request]
    //         }))
    //     }
    // }


    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/')
    }



    render(){
        console.log(this.props)
        return(
            <div className="container">
                <div className="box">
                <div className="columns">
                    <div className="column is-4">
                        <h2 className="subtitle">
                            Campaign Name:
                        </h2>
                        <h2>{this.props.request[0].game.name}</h2>
                        <h3 className="subtitle">Location</h3>
                        <p className="subtitle">{this.props.request[0].game.location}</p>
                        <h3 className="subtitle">exp level:</h3>
                        <p className="subtitle">{this.props.request[0].game.explevel}</p>
                        <h3 className="subtitle">Role Playing Intensity:</h3>
                        <p className="subtitle">{this.props.request[0].game.rplevel}</p>
                    </div>
                    <div className="column is-4">
                        <h2 className="subtitle">
                            From:
                        </h2>
                        <h3 className="title">
                            {this.props.request[0].user.username}
                        </h3>
                        <h3 className="subtitle">
                            Using:
                        </h3>
                        <PartyMember character={this.props.request[0].character} player={this.props.request[0].user}/>
                    </div>
                    <div className="column is-4">
                        <h3 className="subtitle">
                            Message:
                        </h3>
                        <div className="box content">
                            {this.props.request[0].message ? <p>{this.props.request[0].message}</p> : <p>{this.props.request[0].user.username} did not send a message</p>}
                        </div>
                    </div>
                </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Inbox);
