import React from  'react'
import { connect } from 'react-redux'




class Modal extends React.Component{

    state= {
        message: ''
    }

    messageChange= (e) => {
        this.setState({
            message: e.target.value
        })
    }

    handleJoin = () => {
        fetch('http://localhost:3000/requests', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
            },
            body: JSON.stringify({
                game_id: this.props.game.id,
                character_id: this.props.character.id,
                dungeon_master_id: this.props.dm_id,
                message: this.state.message
            })
            }).then(r => r.json()).then(this.props.hideModal(this.props.game.id))
    }

    handleCancel = () => {
        this.props.hideModal(this.props.game.id)
    }



    render(){
        console.log(this.props)
        return(
            <div className="modal-background" style={{zIndex: 600, }}>
                <div className="modal-card">
                    <div className="modal-card-head">
                    <p className="modal-title" style={{textAlign: 'center'}}>Request to Join: </p>
                    <p className="subtitle">{this.props.game.name}</p>
                    </div>
                    <div className="modal-card-body" style={{zIndex: 600}}>
                        <form >
                            <label>Add a Message:</label><br></br>
                        <textarea name="message" cols="80" value={this.state.message} onChange={this.messageChange} style={{height: 200}}></textarea><br></br>
                        <div className="columns">
                            <div className="column">
                            <button className="button is-black" onClick={this.handleJoin}>Send</button> 
                            </div>
                            <div className="column">
                            <button className="button is-black" onClick={this.handleCancel}>Cancel</button> 
                            </div>
                        </div>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Modal);
