import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'



class AllCaracters extends React.Component{

    state = {
        characters: []
    }

    componentDidMount(){
        fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
        .then(r => r.json()).then(user => this.setState({
            characters: user.characters
        }))
        // this.checkCharacters()
    }

    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/login')
    }

    checkCharacters = () => {
        for(let i =0; i < this.state.characters.length; i++){
            if(this.state.characters[i].race === true){
                console.log('checked')
            } else { 
                etch(`http://localhost:3000/characters/${this.state.characters[i].id}`,{
                method: 'DELETE'
                }).then(r => r.json()).then(console.log)
            }
        }
    }



    render(){
        this.checkCharacters()
        console.log(this.state)
        const characters = this.state.characters.map(character  =>
        <div className="box" key={character.id}>
            <h2>Name: {character.name}</h2>
            <h3>Race: {character.race[0].name}</h3>
            <h3>Class: {character.class[0].name}</h3>
            <h4>Level: {character.level}</h4>
            <h4>Alignment: {character.alignment}</h4>
        </div>)
        return(
            <div>
                <NavBar />
                <button onClick={this.handleLogout}>Logout</button>
                <h1>All Characters:</h1>
                {characters}
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
        newCharClass: store.newCharClass,
        newCharRace: store.newCharRace,
        stats: store.stats,
        mods: store.mods,
        skills: store.skills
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      AddClass: (charClass) => {
          dispatch({ type: 'ADD_CLASS', attribute: charClass })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AllCaracters);
