import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'
import CharacterSelect from './CharacterSelect'
import Campaign from './Campaign';
import PartyMember from './PartyMember'
import { Link, Route} from 'react-router-dom'



class AllGames extends React.Component{

    state = {
        AllGames: [],
        myCharacters: []
    }

    componentDidMount(){
        fetch(`http://localhost:3000/games`)
        .then(r => r.json()).then(games => this.setState({
            AllGames: games
        }))
        fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
        .then(r => r.json()).then(user => this.setState({
            myCharacters: user.characters
        }))
    }

    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/')
    }

    handleJoin = (e) => {
        fetch('http://localhost:3000/party_members', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
            },
            body: JSON.stringify({
                game_id: e.target.value,
                character_id: this.props.selectedCharacter.id
            })
        }).then(r => r.json()).then(console.log)
    }

    componentDidUpdate(){
        fetch(`http://localhost:3000/games`)
        .then(r => r.json()).then(games => this.setState({
            AllGames: games
        }))
    }

    getImage = (charClass) => {
        console.log(charClass)
    }




    render(){
        console.log(this.state.AllGames)
        const AllGames = this.state.AllGames.map(game => 
            <div><div key={game.id} className="card columns " >
                <div className="column is-3" >
                    <h1 className="subtitle">Campaign Name:</h1>
                    <p className="title">{game.name}</p>
                    <div className="columns">
                    <h3 className="subtitle column content is-small">Created by:</h3>
                    <div className="column">
                        <img src={require('./boss-key.png')} style={{width:40}}/>
                        <p>{game.dungeon_master.name}</p>
                    </div>
                    </div>
                    <h2 className="subtitle">Description</h2>
                    <p>{game.desc}</p>
                </div>
                <div className="column is-3">
                    <div >
                    <h4 className="subtitle">Location:</h4>
                    <h5 >{game.location}</h5>
                    </div>
                    <div >
                    <h4 className="subtitle">EXP Level:</h4>
                    <h5 > {game.explevel}</h5>
                    </div>
                    <div >
                    <h4 className="subtitle">Role Playing Intensity:</h4>
                    <h5 >{game.rplevel}</h5>
                    {game.party.length < 6 && this.props.selectedCharacter && !game.party_ids.includes(this.props.currentUser.id) ? 
                <div >
                    <h1>Join using:</h1>
                    <PartyMember character={this.props.selectedCharacter} player={this.props.currentUser}/>
                    <button onClick={this.handleJoin} value={game.id}>Join Game</button></div>
                 : null}
                    </div>
                </div>
                <div className="column is-6">
                {game.party_ids.includes(this.props.currentUser.id) || game.dungeon_master.user_id === this.props.currentUser.id ? 
                 <Link to={{pathname:"/Campaign", state: { game: game }}} >
                    <button>Go To Campaign</button>
                    </Link> : null}
                    <h2>Free Spots: {6 - game.party.length}</h2>
                    <h2>Party Members:</h2><br></br>
                    <div className="columns container" >
                        <div className="column">
                        {game.party.length > 0 &&  game.players.length > 0 ? <div>
                            <PartyMember character={game.party[0]} player={game.players[0]}/>
                            </div> : null}
                        {game.party.length > 1 &&  game.players.length > 1 ? <div style={{margin: 5}}>
                            <PartyMember character={game.party[1]} player={game.players[1]}/>
                        </div> : null }
                        </div>
                        <div className="column">
                        {game.party.length > 2 &&  game.players.length > 2 ? <div style={{margin: 5}}>
                            <PartyMember character={game.party[2]} player={game.players[0]}/>
                        </div> : null}
                        {game.party.length > 3 &&  game.players.length > 3 ? <div style={{margin: 5}}>
                            <PartyMember character={game.party[3]} player={game.players[0]}/>
                        </div> : null }
                        </div>
                        <div className="column">
                        {game.party.length > 4 &&  game.players.length > 4 ? <div style={{margin: 5}}>
                            <PartyMember character={game.party[4]} player={game.players[0]}/>
                        </div> : null}
                        {game.party.length > 5 &&  game.players.length > 5 ? <div style={{margin: 5}}>
                            <PartyMember character={game.party[5]} player={game.players[0]}/>
                        </div> : null }
                        </div>
                    </div>
                </div>
            </div>

            </div>
        )
        return(
            <div className="container">
                <NavBar />
                <button onClick={this.handleLogout} className="button is-black" style={{marginLeft: 100}}>Logout</button>
                <div className="container columns box" style={{margin: 50}}>
                    <div className="column">
                    <CharacterSelect characters={this.state.myCharacters} handleCharChange={this.handleCharChange}/>
                    </div>
                    <div className="column">
                        <h1>Currently Selected</h1>
                        <PartyMember character={this.props.selectedCharacter} player={this.props.currentUser}/>
                    </div>
                </div>
                <h1>All Games:</h1><br></br>
                {AllGames}
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
        selectedCharacter: store.selectedCharacter
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      SelectCharacter: (character) => {
          dispatch({
              type: 'CHARACTER_SELECT', character
          })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AllGames);
