import { connect } from 'react-redux'
import ReactDice from 'react-dice-complete'
import 'react-dice-complete/dist/react-dice-complete.css'


class NewCharacter extends React.Component{
    state = {
        strength: 0,
        dexterity: 0,
        constitution: 0,
        intelligence: 0,
        wisdom: 0,
        charisma: 0,
        statRoll: [],
        statVal: 0
    }


