import React from  'react'
import { connect } from 'react-redux'



class NewCharacter extends React.Component{

    state = {
        strMod: 0,
        dexMod: 0,
        conMod: 0,
        intMod: 0,
        wisMod: 0,
        chrMod: 0,
        savingProf: {
            strength: 0,
            dexterity: 0,
            constitution: 0,
            intelligence: 0,
            wisdom: 0,
            charisma: 0
        }
    }

    componentDidMount(){
        // fetch('https://api-beta.open5e.com/backgrounds/')
        // .then(r => r.json())
        // .then(console.log)
        this.getModifiers(this.props.stats)
        const savingProf = this.props.newCharClass.prof_saving_throws.toLowerCase().split(', ')
        this.setState({
            savingProf: {
                [savingProf[0]]: 2,
                [savingProf[1]]: 2
            }
        })

    }

    getModifiers = (stats) => {
        const strMod = this.calculateMod(stats.strength)
        const dexMod = this.calculateMod(stats.dexterity)
        const conMod = this.calculateMod(stats.constitution)
        const intMod = this.calculateMod(stats.intelligence)
        const wisMod = this.calculateMod(stats.wisdom)
        const chrMod = this.calculateMod(stats.charisma) 
        this.setState({
            strMod: strMod,
            dexMod: dexMod,
            conMod: conMod,
            intMod: intMod,
            wisMod: wisMod,
            chrMod: chrMod
        })

    }

    calculateMod = (stat) =>{

        if(stat <= 1){
            return -5
        } else if(2 <= stat && stat <= 3){
            return -4
        } else if(4 <= stat && stat <= 5){
            return -3
        } else if(6 <= stat && stat <= 7){
            return -2
        } else if (8 <= stat && stat <= 9){
            return -1
        } else if ( 10 <= stat && stat <= 11){
            return 0
        } else if (12 <= stat && stat <= 13){
            return 1
        } else if (14 <= stat && stat <= 15){
            return 2
        } else if (16 <= stat && stat <= 17){
            return 3
        } else if (18 <= stat && stat <= 19){
            return 4
        } else if (20 <= stat && stat <= 21){
            return 5
        } else if (22 <= stat && stat <= 23){
            return 6
        } else if (24 <= stat && stat <= 25){
            return 7
        } else if (26 <= stat && stat <= 27){
            return 8
        } else if (28 <= stat && stat <= 29){
            return 9
        } else if (30 <= stat){
            return 10
        }
    }

    render(){
            console.log(this.state)
        return(
            <div className="columns">
                <div className="column">
                <h1>Roll Your Stats:</h1>
                <h2>Chosen Race: {this.props.newCharRace.name}</h2>
                <h2>Chosen Class: {this.props.newCharClass.name}</h2>
                <ul>
                <h2>Stats:</h2>
                <li>Strength - {this.props.stats.strength}</li>
                <li className="box" style={{width: 60}}>{this.state.strMod}</li>
                <li>Dexterity - {this.props.stats.dexterity}</li>
                <li className="box" style={{width: 60}}>{this.state.dexMod}</li>
                <li>Constitution - {this.props.stats.constitution}</li>
                <li className="box" style={{width: 60}}>{this.state.conMod}</li>
                <li>Intelligence - {this.props.stats.intelligence}</li>
                <li className="box" style={{width: 60}}>{this.state.intMod}</li>
                <li>Wisdom - {this.props.stats.wisdom}</li>
                <li className="box" style={{width: 60}}>{this.state.wisMod}</li>
                <li>Charisma - {this.props.stats.charisma}</li>
                <li className="box" style={{width: 60}}>{this.state.chrMod}</li>
                </ul> </div>
                <div className="column">
                <p>Proficiency Modifier:</p>
                <li className="box" style={{width: 100}}>+ 2</li>
                <ul className="box" style={{width: 100}}>
                    <p>Saving Throws</p>
                    <li>Strength: {this.state.strMod + this.state.savingProf.strength}</li>
                </ul>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
      newCharClass: store.newCharClass,
      newCharRace: store.newCharRace,
      stats: store.stats
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      AddRace: (charRace) => {
          dispatch({ type: 'ADD_Race', attribute: charRace })
      },
      AddStrength: (stat) => {
          dispatch({ type: 'ADD_strength', strength: stat})
      },
      AddDexterity: (stat) => {
          dispatch({ type: 'ADD_dexterity', dexterity: stat})
      },
      AddConstitution: (stat) => {
        dispatch({ type: 'ADD_constitution', constitution: stat})
      },
      AddIntelligence: (stat) => {
        dispatch({ type: 'ADD_intelligence', intelligence: stat})
      },
      AddWisdom: (stat) => {
        dispatch({ type: 'ADD_wisdom', wisdom: stat})
      },
      AddCharisma: (stat) => {
        dispatch({ type: 'ADD_charisma', charisma: stat})
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NewCharacter);
