import React from  'react'
import { connect } from 'react-redux'

class Skills extends React.Component{


    render(){
        console.log(this.props)
        return(
            <div className="box" style={{width: 200}}>
                <p>{this.props.newCharClass.name === 'Bard'   ? <button>x</button> : null }{this.props.mods.dexMod} - Acrobatics (Dex)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Barbarian' || "Druid" || 'Fighter' || 'Ranger' ? <button>x</button> : null}{this.props.mods.wisMod} - Animal Handling (Wis)</p>
                <p>{this.props.newCharClass.name === 'Bard' || "Druid" || 'Sorcerer' || 'Warlock' || 'Wizard' ? <button>x</button> : null}{this.props.mods.intMod} - Arcana (Int)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Barbarian' || 'Fighter' || 'Monk' || 'Paladin' || 'Ranger' || 'Rogue' ? <button>x</button> : null}{this.props.mods.strMod} - Athletics (Str)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Rogue' || 'Sorcerer' || 'Warlock' ? <button>x</button> : null}{this.props.mods.chrMod} - Deception (Cha)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Cleric' || 'Fighter' || 'Monk' || 'Warlock' || 'Wizard' ? <button>x</button> : null}{this.props.mods.intMod} - History (Int)</p>
                <p>{this.props.newCharClass.name === 'Bard' ||  'Cleric' || "Druid" || 'Fighter' || 'Monk' || 'Paladin' || 'Ranger' || 'Rogue' || 'Sorcerer' || 'Wizard'? <button>x</button> : null}{this.props.mods.wisMod} - Insight (Wis)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Barbarian' || 'Fighter' || 'Paladin' || 'Rogue' || 'Sorcerer' || 'Warlock' ? <button>x</button> : null}{this.props.mods.chrMod} - Intimidation (Cha</p>)
                <p>{this.props.newCharClass.name === 'Bard' || 'Ranger' || 'Rogue' || 'Warlock' || 'Wizard' ? <button>x</button> : null}{this.props.mods.intMod} - Investigation (Int)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Cleric' || "Druid" || 'Paladin' || 'Wizard' ? <button>x</button> : null}{this.props.mods.wisMod} - Medicine (Wis)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Barbarian' || "Druid" || 'Ranger' ? <button>x</button> : null}{this.props.mods.intMod} - Nature (Int)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Barbarian' || "Druid" || 'Fighter' || 'Ranger' || 'Rogue' ? <button>x</button> : null}{this.props.mods.wisMod} - Perception (Wis)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Rogue' ? <button>x</button> : null}{this.props.mods.chrMod} - Performance (Cha)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Cleric' || 'Paladin' || 'Rogue' || 'Sorcerer' ? <button>x</button> : null}{this.props.mods.chaMod} - Persuasion (Cha)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Cleric' || "Druid" || 'Monk' || 'Paladin' || 'Sorcerer' || 'Warlock' || 'Wizard'? <button>x</button> : null}{this.props.mods.intMod} - Religion (Int)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Rogue' ? <button>x</button> : null}{this.props.mods.dexMod} - Sleight of Hand (Dex)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Monk' || 'Ranger' || 'Rogue' ? <button>x</button> : null}{this.props.mods.dexMod} - Stealth (Dex)</p>
                <p>{this.props.newCharClass.name === 'Bard' || 'Barbarian' || "Druid" || 'Fighter' || 'Ranger' ? <button>x</button> : null}{this.props.mods.wisMod} - Survival (Wis)</p>
            </div>
        )
    }

}


const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
      newCharClass: store.newCharClass,
      newCharRace: store.newCharRace,
      stats: store.stats,
      mods: store.mods
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      AddRace: (charRace) => {
          dispatch({ type: 'ADD_Race', attribute: charRace })
      },
      AddStrength: (stat) => {
          dispatch({ type: 'ADD_strength', strength: stat})
      },
      AddDexterity: (stat) => {
          dispatch({ type: 'ADD_dexterity', dexterity: stat})
      },
      AddConstitution: (stat) => {
        dispatch({ type: 'ADD_constitution', constitution: stat})
      },
      AddIntelligence: (stat) => {
        dispatch({ type: 'ADD_intelligence', intelligence: stat})
      },
      AddWisdom: (stat) => {
        dispatch({ type: 'ADD_wisdom', wisdom: stat})
      },
      AddCharisma: (stat) => {
        dispatch({ type: 'ADD_charisma', charisma: stat})
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Skills);
