import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'
import CharacterSelect from './CharacterSelect'



class Campaign extends React.Component{

    render(){
        console.log(this.props.location.state.game)

        return(
            <div>
                <NavBar />
                <div className="columns">
                    <div className="column">
                        <h1 className="title">{this.props.location.state.game.name}</h1>
                        <h2>Location: {this.props.location.state.game.location}</h2>
                        <h2>Dungeon Master: {this.props.location.state.game.dungeon_master.name}</h2>
                        <h2>Description: {this.props.location.state.game.desc}</h2>
                        <h2>Role Playing Intensity: {this.props.location.state.game.rplevel}</h2>
                        <h2>Experience Level: {this.props.location.state.game.explevel}</h2>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
        selectedCharacter: store.selectedCharacter
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Campaign);
