import React from  'react'
import { connect } from 'react-redux'
import ReactDice from 'react-dice-complete'
import 'react-dice-complete/dist/react-dice-complete.css'


class NewCharacter extends React.Component{
    state = {
        strength: 0,
        dexterity: 0,
        constitution: 0,
        intelligence: 0,
        wisdom: 0,
        charisma: 0,
        statRoll: []
    }


    rollAll() {
        // this.reactDice.rollAll()
        if(this.state.statRoll.length === 4){
            console.log(this.state.statRoll)
            return 
        } else {
            this.reactDice.rollAll()
        }
    }

    rollDoneCallback(num) {
        this.setState({
            statRoll: [...this.state.statRoll, num]
        })
    }



    render(){

        return(
            <div className="columns">
                <div className="column">
                <h1>Roll Your Stats:</h1>
                <h2>Chosen Race: {this.props.newCharRace.name}</h2>
                <h2>Chosen Class: {this.props.newCharClass.name}</h2>
                <ul>
                <h2>Stats:</h2>
                <li>Strength</li>
                <li className="box" style={{width: 60}}>{this.state.strength}</li>
                <li>Dexterity</li>
                <li className="box" style={{width: 60}}>{this.state.dexterity}</li>
                <li>Constitution</li>
                <li className="box" style={{width: 60}}>{this.state.constitution}</li>
                <li>Intelligence</li>
                <li className="box" style={{width: 60}}>{this.state.intelligence}</li>
                <li>Wisdom</li>
                <li className="box" style={{width: 60}}>{this.state.wisdom}</li>
                <li>Charisma</li>
                <li className="box" style={{width: 60}}>{this.state.charisma}</li>
                </ul> </div>
                <div className="column">
                <ReactDice
                    numDice={1}
                    rollDone={this.rollDoneCallback}
                    ref={dice => this.reactDice = dice}
                    faceColor={'#696969'}
                    dotColor={'#D3D3D3'}
                    />
                </div>
                <div className="column">
                <ReactDice
                    numDice={1}
                    rollDone={this.rollDoneCallback}
                    ref={dice => this.reactDice = dice}
                    faceColor={'#696969'}
                    dotColor={'#D3D3D3'}
                    />
                </div>
                <div className="column">
                <ReactDice
                    numDice={1}
                    rollDone={this.rollDoneCallback}
                    ref={dice => this.reactDice = dice}
                    faceColor={'#696969'}
                    dotColor={'#D3D3D3'}
                    />
                </div>
                <div className="column">
                <ReactDice
                    numDice={1}
                    rollDone={this.rollDoneCallback}
                    ref={dice => this.reactDice = dice}
                    faceColor={'#696969'}
                    dotColor={'#D3D3D3'}
                    />
                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
      newCharClass: store.newCharClass,
      newCharRace: store.newCharRace
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      AddRace: (charRace) => {
          dispatch({ type: 'ADD_Race', attribute: charRace })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NewCharacter);
