import React from  'react'
import { connect } from 'react-redux'



class NewCharacter extends React.Component{

    componentDidMount(){
        this.getModifiers(this.props.stats)
    }

    getModifiers = (stats) => {
        const strMod = this.calculateMod(stats.strength)
        console.log(strMod)

    }

    calculateMod = (stat) =>{
        console.log(stat)
        switch(stat){
        case (stat < 2):
            return -5
        case (1 < stat < 4):
            return -4
        case(5 < stat < 8):
            return -3
        case (7 <= stat <= 10):
            return -2
        case (9 < stat < 12):
            return 0
        case ( 11 < stat < 14):
            return 1
        case (13 < stat < 16):
            return 2
        case (15 < stat < 18):
            return 3
        case (17 < stat < 20):
            return 4
        case (19 < stat < 22):
            return 5
        case (21 < stat < 24):
            return 6
        case (23 < stat < 26):
            return 7
        case (25 < stat < 28):
            return 8
        case (27 < stat < 29):
            return 9
        case (30 < stat):
            return 10
        default:
            return 0
        }
    }

    render(){
            console.log(this.props)
        return(
            <div className="columns">
                <div className="column">
                <h1>Roll Your Stats:</h1>
                <h2>Chosen Race: {this.props.newCharRace.name}</h2>
                <h2>Chosen Class: {this.props.newCharClass.name}</h2>
                <ul>
                <h2>Stats:</h2>
                <li>Strength</li>
                <li className="box" style={{width: 60}}>{this.props.strength}</li>
                <li>Dexterity</li>
                <li className="box" style={{width: 60}}>{this.props.dexterity}</li>
                <li>Constitution</li>
                <li className="box" style={{width: 60}}>{this.props.constitution}</li>
                <li>Intelligence</li>
                <li className="box" style={{width: 60}}>{this.props.intelligence}</li>
                <li>Wisdom</li>
                <li className="box" style={{width: 60}}>{this.props.wisdom}</li>
                <li>Charisma</li>
                <li className="box" style={{width: 60}}>{this.props.charisma}</li>
                </ul> </div>
                <div className="column">
                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
      newCharClass: store.newCharClass,
      newCharRace: store.newCharRace,
      stats: store.stats
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      AddRace: (charRace) => {
          dispatch({ type: 'ADD_Race', attribute: charRace })
      },
      AddStrength: (stat) => {
          dispatch({ type: 'ADD_strength', strength: stat})
      },
      AddDexterity: (stat) => {
          dispatch({ type: 'ADD_dexterity', dexterity: stat})
      },
      AddConstitution: (stat) => {
        dispatch({ type: 'ADD_constitution', constitution: stat})
      },
      AddIntelligence: (stat) => {
        dispatch({ type: 'ADD_intelligence', intelligence: stat})
      },
      AddWisdom: (stat) => {
        dispatch({ type: 'ADD_wisdom', wisdom: stat})
      },
      AddCharisma: (stat) => {
        dispatch({ type: 'ADD_charisma', charisma: stat})
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NewCharacter);
