export default (state={ currentUser: {} }, action) => {
    // console.log("our reducer's state: ", state)
    // console.log("our reducer's action: ", action)

    switch (action.type) {
      case "LOGIN_CURRENT_USER":
        return { currentUser: action.user }

      default:
        return state
    }
  }
