        return(
            <div className="container">
                <div className="box">
                <div className="columns">
                    <div className="column is-4">
                        <h2 className="subtitle">
                            Campaign Name:
                        </h2>
                        <h2>{this.props.request[0].game.name}</h2>
                        <h3 className="subtitle">Location</h3>
                        <p>{this.props.request[0].game.location}</p>
                        <h3 className="subtitle">exp level:</h3>
                        <p>{this.props.request[0].game.explevel}</p>
                        <h3 className="subtitle">Role Playing Intensity:</h3>
                        <p>{this.props.request[0].game.rplevel}</p>
                    </div>
                </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Inbox);
