import React from  'react'
import { connect } from 'react-redux'

class CharacterSelect extends React.Component{

    handleChange = (e) => {
        console.log(e.target.value)
    }


    render(){
        const charBtns = this.props.characters.map((character, index) => <option onChange={this.handleChange} key={character.id} value={index}>{character.name} - Class: {character.class[0].name} - Race: {character.race[0].name} - Alignment: {character.alignment}</option>)
        return(
            <div className="box" style={{ width: 600 }}>
                <form className="box">
                    <label>Select Current Character</label>
                    <select >
                        {charBtns}
                    </select>
                </form>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
        selectedCharacter: store.selectedCharacter
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      SelectCharacter: (character) => {
          dispatch({
              type: 'CHARACTER_SELECT', character
          })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CharacterSelect);
