import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'



class AllGames extends React.Component{

    state = {
        AllGames: []
    }

    componentDidMount(){
        fetch(`http://localhost:3000/games`)
        .then(r => r.json()).then(games => this.setState({
            AllGames: games
        }))
    }

    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/login')
    }





    render(){
        console.log(this.state)
        const AllGames = this.state.AllGames.map(game => 
            <div key={game.id} className="box" className="columns">
                <div className="column">
                    <h1>Campaign Name:</h1>
                    <p>{game.name}</p>
                    <h2>EXP Level:</h2>
                    <p>{game.explevel}</p>
                    <h2>Role Playing Intensity:</h2>
                    <p>{game.rplevel}</p>
                    <h3>Created by:</h3>
                    <p>{game.dungeon_master.username}</p>
                </div>
                <div className="column">
                    <h2>Free Spots: {6 - game.party.length}</h2>
                    <h2>Party Members:</h2>
                    <div className="columns">
                        {game.party.length > 0 &&  game.players.length > 0 ? <div className="column" className="box">
                            <p>Name: {game.party[0].name}</p>
                            <p>Class: {game.party[0].class}</p>
                            <p>User: {game.players[0].username}</p>
                        </div> : null}
                        {game.party.length > 1 &&  game.players.length > 1 ? <div className="box">
                            <p>Name: {game.party[1].name}</p>
                            <p>Class: {game.party[1].class}</p>
                            <p>User: {game.players[1].username}</p>
                        </div> : null }
                        {game.party.length > 2 &&  game.players.length > 2 ? <div className="column" className="box">
                            <p>Name: {game.party[2].name}</p>
                            <p>Class: {game.party[2].class}</p>
                            <p>User: {game.players[2].username}</p>
                        </div> : null}
                        {game.party.length > 3 &&  game.players.length > 3 ? <div className="box">
                            <p>Name: {game.party[3].name}</p>
                            <p>Class: {game.party[3].class}</p>
                            <p>User: {game.players[3].username}</p>
                        </div> : null }
                        {game.party.length > 4 &&  game.players.length > 4 ? <div className="column" className="box">
                            <p>Name: {game.party[4].name}</p>
                            <p>Class: {game.party[4].class}</p>
                            <p>User: {game.players[4].username}</p>
                        </div> : null}
                        {game.party.length > 5 &&  game.players.length > 5 ? <div className="box">
                            <p>Name: {game.party[5].name}</p>
                            <p>Class: {game.party[5].class}</p>
                            <p>User: {game.players[5].username}</p>
                        </div> : null }
                    </div>
                </div>
            </div>
        )
        return(
            <div>
                <NavBar />
                <button onClick={this.handleLogout}>Logout</button>
                <h1>All Games:</h1>
                {AllGames}
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AllGames);
