import React from  'react'
import { connect } from 'react-redux'

class Skills extends React.Component{


    render(){
        console.log(this.props)
        return(
            <div className="box" style={{width: 200}}>
                <p>Acrobatics (Dex) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Fighter' || this.props.newCharClass.name ==='Monk' || this.props.newCharClass.name ==='Rogue'  ? <button>x</button> : null }{this.props.mods.dexMod}</p>
                <p>Animal Handling (Wis) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Barbarian' || this.props.newCharClass.name ==="Druid" || this.props.newCharClass.name ==='Fighter' || this.props.newCharClass.name ==='Ranger' ? <button>x</button> : null}{this.props.mods.wisMod}</p>
                <p>Arcana (Int) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==="Druid" || this.props.newCharClass.name ==='Sorcerer' || this.props.newCharClass.name ==='Warlock' || this.props.newCharClass.name ==='Wizard' ? <button>x</button> : null}{this.props.mods.intMod}</p>
                <p>Athletics (Str) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Barbarian' || this.props.newCharClass.name ==='Fighter' || this.props.newCharClass.name ==='Monk' || this.props.newCharClass.name ==='Paladin' || this.props.newCharClass.name ==='Ranger' || this.props.newCharClass.name ==='Rogue' ? <button>x</button> : null}{this.props.mods.strMod}</p>
                <p>Deception (Cha) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Rogue' || this.props.newCharClass.name ==='Sorcerer' || this.props.newCharClass.name ==='Warlock' ? <button>x</button> : null}{this.props.mods.chrMod}</p>
                <p>History (Int) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Cleric' || this.props.newCharClass.name ==='Fighter' || this.props.newCharClass.name ==='Monk' || this.props.newCharClass.name ==='Warlock' || this.props.newCharClass.name ==='Wizard' ? <button>x</button> : null}{this.props.mods.intMod}</p>
                <p>Insight (Wis) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name === 'Cleric' || this.props.newCharClass.name ==="Druid" || this.props.newCharClass.name ==='Fighter' || this.props.newCharClass.name ==='Monk' || this.props.newCharClass.name ==='Paladin' || this.props.newCharClass.name ==='Ranger' || this.props.newCharClass.name ==='Rogue' || this.props.newCharClass.name ==='Sorcerer' || this.props.newCharClass.name ==='Wizard'? <button>x</button> : null}{this.props.mods.wisMod}</p>
                <p>Intimidation (Cha) {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Barbarian' || this.props.newCharClass.name ==='Fighter' || this.props.newCharClass.name ==='Paladin' || this.props.newCharClass.name ==='Rogue' || this.props.newCharClass.name ==='Sorcerer' || this.props.newCharClass.name ==='Warlock' ? <button>x</button> : null}{this.props.mods.chrMod}</p>
                <p>Investigation (Int) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Ranger' || this.props.newCharClass.name ==='Rogue' || this.props.newCharClass.name ==='Warlock' || this.props.newCharClass.name ==='Wizard' ? <button>x</button> : null}{this.props.mods.intMod}</p>
                <p>Medicine (Wis) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Cleric' || this.props.newCharClass.name ==="Druid" || this.props.newCharClass.name ==='Paladin' || this.props.newCharClass.name ==='Wizard' ? <button>x</button> : null}{this.props.mods.wisMod}</p>
                <p>Nature (Int) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Barbarian' || this.props.newCharClass.name ==="Druid" || this.props.newCharClass.name ==='Ranger' ? <button>x</button> : null}{this.props.mods.intMod}</p>
                <p>Perception (Wis) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Barbarian' || this.props.newCharClass.name ==="Druid" || this.props.newCharClass.name ==='Fighter' || this.props.newCharClass.name ==='Ranger' || this.props.newCharClass.name ==='Rogue' ? <button>x</button> : null}{this.props.mods.wisMod}</p>
                <p>Performance (Cha) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Rogue' ? <button>x</button> : null}{this.props.mods.chrMod}</p>
                <p>Persuasion (Cha) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Cleric' || this.props.newCharClass.name ==='Paladin' || this.props.newCharClass.name ==='Rogue' || this.props.newCharClass.name ==='Sorcerer' ? <button>x</button> : null}{this.props.mods.chaMod}</p>
                <p>Religion (Int) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Cleric' || this.props.newCharClass.name ==="Druid" || this.props.newCharClass.name ==='Monk' || this.props.newCharClass.name ==='Paladin' || this.props.newCharClass.name ==='Sorcerer' || this.props.newCharClass.name ==='Warlock' || this.props.newCharClass.name ==='Wizard'? <button>x</button> : null}{this.props.mods.intMod}</p>
                <p>Sleight of Hand (Dex) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Rogue' ? <button>x</button> : null}{this.props.mods.dexMod} - Sleight of Hand (Dex)</p>
                <p>Stealth (Dex) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Monk' || this.props.newCharClass.name ==='Ranger' || this.props.newCharClass.name ==='Rogue' ? <button>x</button> : null}{this.props.mods.dexMod}</p>
                <p>Survival (Wis) - {this.props.newCharClass.name === 'Bard' || this.props.newCharClass.name ==='Barbarian' || this.props.newCharClass.name ==="Druid" || this.props.newCharClass.name ==='Fighter' || this.props.newCharClass.name ==='Ranger' ? <button>x</button> : null}{this.props.mods.wisMod}</p>
            </div>
        )
    }

}


const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
      newCharClass: store.newCharClass,
      newCharRace: store.newCharRace,
      stats: store.stats,
      mods: store.mods
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      AddRace: (charRace) => {
          dispatch({ type: 'ADD_Race', attribute: charRace })
      },
      AddStrength: (stat) => {
          dispatch({ type: 'ADD_strength', strength: stat})
      },
      AddDexterity: (stat) => {
          dispatch({ type: 'ADD_dexterity', dexterity: stat})
      },
      AddConstitution: (stat) => {
        dispatch({ type: 'ADD_constitution', constitution: stat})
      },
      AddIntelligence: (stat) => {
        dispatch({ type: 'ADD_intelligence', intelligence: stat})
      },
      AddWisdom: (stat) => {
        dispatch({ type: 'ADD_wisdom', wisdom: stat})
      },
      AddCharisma: (stat) => {
        dispatch({ type: 'ADD_charisma', charisma: stat})
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Skills);
