import React from  'react'
import { connect } from 'react-redux'
import ClassInfo from './ClassInfo'

class NewCharacter extends React.Component{
    state = {
        allClasses: [],
        currentClass: 'barbarian'
    }

    componentDidMount(){
        fetch('http://localhost:3000/profile',{
        headers: {
          'Authorization': `Bearer ${localStorage.token}`
        }
        })
        .then(res => res.json())
        .then(user => this.props.LogIn(user)
        )
        fetch('https://api-beta.open5e.com/classes/')
        .then(r => r.json())
        .then(data => this.setState({
            allClasses: data.results
        }))
    }


    render(){
        const classComps = this.state.allClasses.map(charClass => <ClassInfo key={charClass.id} charClass={charClass} /> )
        return(
            <div>
                <h1>Choose Your Class:</h1>
                {classComps}
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NewCharacter);
