import React, { Component } from 'react';
import { connect } from 'react-redux'
import {Link} from 'react-router-dom'
import NavBar from './NavBar';
import PartyMember from './PartyMember'


class ProfilePage extends Component {

    state = {
        username: '',
        AllGames: []
    }

  handleClick = () => {
    localStorage.clear()
    this.props.history.push('/')
  }

  componentDidMount(){
    fetch('http://localhost:3000/profile',{
    headers: {
      'Authorization': `Bearer ${localStorage.token}`
    }
    })
    .then(res => res.json())
    .then(user => 
      {this.props.LogIn(user)

        fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
        .then(r => r.json()).then(user => this.props.SelectCharacter(user.characters[0]))}
    )
    localStorage.removeItem('newCharClass')
    localStorage.removeItem('newCharRace')
    localStorage.removeItem('stats')
    localStorage.removeItem('mods')
    localStorage.removeItem('skills')
    this.checkCharacters()
    fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
        .then(r => r.json()).then(user => this.getGames(user.games))
  }

  getGames = (games) => {
    const gameArr = games.filter(function(ele){
      if(ele.game.length === 1){
        return ele
      }
    })
    for(let i =0; i < gameArr.length; i++){
      fetch(`http://localhost:3000/games/${gameArr[i].game[0].id}`)
      .then(r => r.json())
      .then(games => console.log(games))
    }
  }

  checkCharacters = () => {
    fetch(`http://localhost:3000/users/${this.props.currentUser.id}`)
    .then(r => r.json())
    .then(user => this.setState({
        AllCharacters: user.characters
    }, () => {
        for(let i = 0; i < this.state.AllCharacters.length; i++){
            if(this.state.AllCharacters[i].race.length > 0 && this.state.AllCharacters[i].class.length > 0){
                console.log('checked')
            } else {
                fetch(`http://localhost:3000/characters/${this.state.AllCharacters[i].id}`, {
                    method: 'DELETE'
                }).then(r => r.json()).then(console.log('deleted'))
            }
        }
    }))
  }



  render() {
    console.log(this.state)
    const AllGames = this.state.AllGames.map(game => 
      <div><div key={game.id} className="box columns" style={{margin: 50}}>
          <div className="column" style={{margin: 20}}>
              <h1 className="subtitle">Campaign Name:</h1>
              <p className="title">{game.name}</p>
              <div className="columns">
              <h3 className="subtitle column">Created by:</h3>
              <div className="column">
                  <img src={require('./boss-key.png')} style={{width:40}}/>
                  <p>{game.dungeon_master.name}</p>
              </div>
              </div>
              <h2 className="subtitle">Description</h2>
              <p>{game.desc}</p>
          </div>
          <div className="column">
              <div style={{margin: 5}}>
              <h4 className="subtitle">Location:</h4>
              <h5 style={{marginBottom: 25}}>{game.location}</h5>
              </div>
              <div style={{margin: 5}}>
              <h4 className="subtitle">EXP Level:</h4>
              <h5 style={{marginBottom: 25}}> {game.explevel}</h5>
              </div>
              <div style={{margin: 5}}>
              <h4 className="subtitle">Role Playing Intensity:</h4>
              <h5 style={{marginBottom: 25}}>{game.rplevel}</h5>
              </div>
          </div>
          <div className="column">
          {game.party_ids.includes(this.props.currentUser.id) || game.dungeon_master.user_id === this.props.currentUser.id ? 
           <Link to={{pathname:"/Campaign", state: { game: game }}} >
              <button>Go To Campaign</button>
              </Link> : null}
              <h2>Free Spots: {6 - game.party.length}</h2>
              <h2>Party Members:</h2><br></br>
              <div className="columns container" style={{width: 1300}}>
                  <div className="column">
                  {game.party.length > 0 &&  game.players.length > 0 ? <div>
                      <PartyMember character={game.party[0]} player={game.players[0]}/>
                      </div> : null}
                  {game.party.length > 1 &&  game.players.length > 1 ? <div style={{margin: 5}}>
                      <PartyMember character={game.party[1]} player={game.players[1]}/>
                  </div> : null }
                  </div>
                  <div className="column">
                  {game.party.length > 2 &&  game.players.length > 2 ? <div style={{margin: 5}}>
                      <PartyMember character={game.party[2]} player={game.players[0]}/>
                  </div> : null}
                  {game.party.length > 3 &&  game.players.length > 3 ? <div style={{margin: 5}}>
                      <PartyMember character={game.party[3]} player={game.players[0]}/>
                  </div> : null }
                  </div>
                  <div className="column">
                  {game.party.length > 4 &&  game.players.length > 4 ? <div style={{margin: 5}}>
                      <PartyMember character={game.party[4]} player={game.players[0]}/>
                  </div> : null}
                  {game.party.length > 5 &&  game.players.length > 5 ? <div style={{margin: 5}}>
                      <PartyMember character={game.party[5]} player={game.players[0]}/>
                  </div> : null }
                  </div>
              </div>
          </div>
      </div>

      </div>)
    return (
      <div className="container">
        <NavBar />
        <button onClick={this.handleClick}>Logout</button>
        {
          this.props.currentUser.username ?
          <h1 className="title">Welcome {this.props.currentUser.username}!</h1> :
          <h1>getting your info...</h1>

        }

      </div>
    );
  }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
      SelectedCharacter: store.SelectedCharacter
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      SelectCharacter: (character) => {
          dispatch({
              type: 'CHARACTER_SELECT', character
          })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ProfilePage);
