import React from  'react'
import NavBar from './NavBar'

export default class CharacterSelect extends React.Component{

    handleChange = (e) => {
        console.log(e.target.value)
    }
    render(){
        const charBtns = this.props.characters.map(character => <option key={character.id} value={character}>{character.name}</option>)
        return(
            <div className="box" style={{ width: 200 }}>
                <form className="box">
                    <select onChange={this.handleChange}>
                        {charBtns}
                    </select>
                </form>
            </div>
        )
    }
}
