import React from 'react'

class RaceInfo extends React.Component{

    handleClick = () => {
        this.props.saveClass(this.props.charClass)
    }

    render(){
        const asiLis = this.props.charRace.asi.map(asi => `${asi.attributes[0]} - ${asi.value}`)
        console.log(asiLis)
        const raceDesc = this.props.charRace.desc.split('##')
        // console.log(classDesc)
        return(
            <div className="container box">
                <h2 className='title'>{this.props.charRace.name}</h2>
                <button onClick={this.handleClick}>Choose Race</button>
                <p className="content">{raceDesc}</p>
                <p>Age: {this.props.charRace.age}</p>
                {/* <p>Ability score increase: {this.props.charRace.asi[0].attributes[0]}</p> */}
            </div>
        )
    }
}

export default RaceInfo
