import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'
import Request from'./Request'



class Inbox extends React.Component{


    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/')
    }



    render(){
        console.log(this.props.requests)
        const requestComps = this.props.requests.map( request => <Request request={request}/>)
        return(
            <div className="container">
                <NavBar />
                <button onClick={this.handleLogout} className="button is-black" style={{marginLeft: 100, }}>Logout</button>
                <div className="title">
                    <h1>Inbox:</h1>
                    {this.props.requests.length >= 0 ? requestComps : <p>You have no requests at this time</p>}

                </div>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser,
      requests: store.requests
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Inbox);
