import React from 'react'

class StatDist extends React.Component {

    state = {
        statVals: [],
        strength: false,
        dexterity:false,
        constitution: false,
        intelligence: false,
        wisdom: false,
        charisma: false

    }

    handleClick = (e) => {
        this.props.getStat(e.target.value)
        this.setState({
            [e.target.value]: true
        })
    }


    render(){
        return(
            <div>
                {this.state.strClick === false ? <button onClick={this.handleClick}  value="strength">Strength</button> : null }
                {this.state.dexClick === false ? <button value='dexterity'  onClick={this.handleClick}>Dexterity</button> : null }
                {this.state.conClick === false ? <button value='constitution' onClick={this.handleClick} >Constitution</button> : null }
                {this.state.intClick === false ? <button value="intelligence" onClick={this.handleClick}>Intelligence</button> : null }
                {this.state.wisClick === false ? <button value='wisdom'  onClick={this.handleClick}>Wisdom</button> : null }
                {this.state.chrClick === false ? <button value='charisma' onClick={this.handleClick}>Charisma</button> : null }
            </div>
        )
    }
}

export default StatDist
