import React from  'react'
import { connect } from 'react-redux'
import ClassInfo from './ClassInfo'


class NewCharacter extends React.Component{
    state = {
        allClasses: [],
        currentClass: 'barbarian'
    }

    componentDidMount(){
        fetch('http://localhost:3000/profile',{
        headers: {
          'Authorization': `Bearer ${localStorage.token}`
        }
        })
        .then(res => res.json())
        .then(user => this.props.LogIn(user)
        )
        fetch('https://api-beta.open5e.com/classes/')
        .then(r => r.json())
        .then(data => this.setState({
            allClasses: data.results
        }))
    }

    handleChange = (e) =>{
        const newClass = e.target.value
        this.setState({
            currentClass: newClass
        })
    }

    showClass = () =>{
        const classComps = this.state.allClasses.map(charClass => <ClassInfo key={charClass.id} charClass={charClass} /> )
        // switch(this.state.currentClass){
        //     case 'barbarian':
        //         return classComps[0]
        //     case 'bard':
        //         return classComps[1]
        //     case 'cleric':
        //         return classComps[2]
        //     case 'druid':
        //         return classComps[3]
        //     case 'fighter':
        //         return classComps[4]
        //     case 'monk':
        //         return classComps[5]
        //     case 'paladin':
        //         return classComps[6]
        //     case 'ranger':
        //         return classComps[7]
        //     case 'rogue':
        //         return classComps[8]
        //     case 'sorcerer':
        //         return classComps[9]
        //     case 'warlock':
        //         return classComps[10]
        //     case 'wizard':
        //         return classComps[11]

        //     default:
        //         return classComps[0]
        // }
        if(this.state.currentClass === 'barbarian'){
            return classComps[0]
        }
    }



    render(){
        // console.log(this.state.currentClass)
        const classComps = this.state.allClasses.map(charClass => <ClassInfo key={charClass.id} charClass={charClass} /> )
        return(
            <div>
                <h1>Choose Your Class:</h1>
                <select onChange={this.handleChange}>
                    <option value="barbarian">Barbarian</option>
                    <option value="bard">Bard</option>
                    <option value="cleric">Cleric</option>
                    <option value="druid">Druid</option>
                    <option value="fighter">Fighter</option>
                    <option value="monk">Monk</option>
                    <option value="paladin">Paladin</option>
                    <option value="ranger">Ranger</option>
                    <option value="rogue">Rogue</option>
                    <option value="sorcerer">Sorcerer</option>
                    <option value="warlock">Warlock</option>
                    <option value="wizard">Wizard</option>
                </select>
                {this.showClass}
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
      currentUser: store.currentUser
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NewCharacter);
