import React from  'react'
import NavBar from './NavBar'

export default class CharacterSelect extends React.Component{

    handleChange = (e) => {
        console.log(e.target.value)
    }
    render(){
        const charBtns = this.props.characters.map(character => <option key={character.id} value={character}>{character.name} - Class: {character.class[0].name} - Race: {character.race[0].name} - Alignment: {character.alignment}</option>)
        return(
            <div className="box" style={{ width: 600 }}>
                <form className="box">
                    <label>Select Current Character</label>
                    <select onChange={this.handleChange}>
                        {charBtns}
                    </select>
                </form>
            </div>
        )
    }
}
