import React from 'react'
import { render } from 'react-dom'
import { connect } from 'react-redux'
import { Router, Route, Link } from 'react-router-dom'

class NavBar extends React.Component{

    checkCharacters = () => {
        fetch(`http://localhost:3000/users/${this.props.currentUser}`)
        .then()
    }


    render(){
        return(
            <div>
                <ul>
                    <Link to={{pathname:'./profile'}}>Profile Page</Link>
                    <Link to={{pathname:'./AllCharacters'}} onClick={this.checkCharacters}>All Characters</Link>
                    <Link to={{pathname:'./NewClass'}}>Create New Character</Link>
                </ul>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,
        newCharClass: store.newCharClass,
        newCharRace: store.newCharRace,
        stats: store.stats,
        mods: store.mods,
        skills: store.skills
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      },
      AddClass: (charClass) => {
          dispatch({ type: 'ADD_CLASS', attribute: charClass })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NavBar);
