import React from 'react'

class StatDist extends React.Component {

    state = {
        statVals: [],
        strClick: false,
        dexClick:false,
        conClick: false,
        intClick: false,
        wisClick: false,
        chrClick: false

    }

    handleClick = (e) => {
        this.props.getStat(e.target.value)
    }
    strClick = () => {
        this.setState({
            strClick: true
        })
    }
    render(){
        return(
            <div>
                {this.state.strClick === false ? <button onClick={this.handleClick} onClick={this.strClick} value="strength">Strength</button> : null }
                {this.state.dexClick === 0 ? <button value='dexterity' onClick={this.handleClick}>Dexterity</button> : null }
                {this.state.conClick === 0 ? <button value='constitution' onClick={this.handleClick}>Constitution</button> : null }
                {this.state.intClick === 0 ? <button value="intelligence" onClick={this.handleClick}>Intelligence</button> : null }
                {this.state.wisClick === 0 ? <button value='wisdom' onClick={this.handleClick}>Wisdom</button> : null }
                {this.state.chrClick === 0 ? <button value='charisma' onClick={this.handleClick}>Charisma</button> : null }
            </div>
        )
    }
}

export default StatDist
