function rootReducer( state = {}, action){
    switch(action.type){
        case "LOGIN_CURRENT_USER":
            return {...state, currentUser: action.user}
        case "ADD_CLASS":
            return {...state, newCharClass: action.attribute }
        case "ADD_Race":
            return {...state, newCharRace: action.attribute }
        case "ADD_STATS":
            return {...state, stats: {
                strength: action.strength,
                dexterity: action.dexterity,
                constitution: action.constitution,
                intellegence: action.intellegence,
                wisdom: action.wisdom,
                charisma: action.charisma
            }}

        default: 
        return state
    }
}
