import React from 'react'

class StatDist extends React.Component {

    state = {
        statVals: [],
        strClick: false,
        dexClick:false,
        conClick: false,
        intClick: false,
        wisClick: false,
        chrClick: false

    }

    handleClick = (e) => {
        this.props.getStat(e.target.value)
    }

    strClick = () => {
        this.setState({
            strClick: true
        })
    }

    dexClick = () => {
        this.setState({
            dexClick: true
        })
    }

    conClick = () => {
        this.setState({
            conClick: true
        })
    }

    intClick = () => {
        this.setState({
            intClick: true
        })
    }

    wisClick = () => {
        this.setState({
            wisClick: true
        })
    }

    chrClick = () => {
        this.setState({
            chrClick: true
        })
    }

    render(){
        return(
            <div>
                {this.state.strClick === false ? <button onClick={this.handleClick} onClick={this.strClick} value="strength">Strength</button> : null }
                {this.state.dexClick === false ? <button value='dexterity' onClick={this.dexClick} onClick={this.handleClick}>Dexterity</button> : null }
                {this.state.conClick === false ? <button value='constitution' onClick={this.handleClick} onClick={this.conClick}>Constitution</button> : null }
                {this.state.intClick === false ? <button value="intelligence" onClick={this.handleClick} onClick={this.intClick}>Intelligence</button> : null }
                {this.state.wisClick === false ? <button value='wisdom' onClick={this.wisClick} onClick={this.handleClick}>Wisdom</button> : null }
                {this.state.chrClick === false ? <button value='charisma' onClick={this.chrClick} onClick={this.handleClick}>Charisma</button> : null }
            </div>
        )
    }
}

export default StatDist
