import React from  'react'
import { connect } from 'react-redux'
import NavBar from './NavBar'

class NewGame extends React.Component{

    state = {
        title: ''
    }


    handleLogout = () => {
        localStorage.clear()
        this.props.history.push('/login')
    }


    titleChange = (e) => {
        this.setState({
            title: e.target.value
        })
    } 


    render(){
        console.log(this.state.title)
        return(
            <div>
                <NavBar />
                <button onClick={this.handleLogout}>Logout</button>
                <h1>New Game</h1>
                <form>
                    <div className="box">
                        <label>Campaign Name:</label>
                        <input type="text" placholder="title" value={this.state.title} onChange={this.titleChange}/>
                    </div>
                    <div className="box">
                        <label>Experience Level:</label>
                        <select>
                            <option>Anyone is Welcome</option>
                            <option>Some Experience Preferred</option>
                            <option>Veterans</option>
                        </select>
                    </div>
                    <div className="box">
                        <label>Role Playing Intensity:</label>
                        <select>
                            <option>As intense (or not) as you want</option>
                            <option>We won't be breaking character</option>
                        </select>
                    </div>
                </form>

            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        currentUser: store.currentUser,

    }
}

const mapDispatchToProps = (dispatch) => {
    return {
      LogIn: (loggedInUser) => {
        dispatch({ type: 'LOGIN_CURRENT_USER', user: loggedInUser })
      }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NewGame)
